raw.css:
img {
  @apply h-full w-full object-cover object-center rounded-xl;
}

.root {
  @apply grid gap-12;
  grid-template-columns: auto 1fr 1fr;
  grid-template-rows: auto auto;

  .title {
    grid-column-start: 1;
    grid-column-end: 3;
  }

  .bottom-left {
    grid-row-start: 2;
    grid-row-end: 4;
    grid-column-start: 2;
  }

  .top-right {
    @apply mb-12;
    grid-row-start: 1;
    grid-row-end: 4;
    grid-column-start: 3;
  }

  .footer {
    grid-row-start: 4;
    grid-column-start: 2;
    grid-column-end: -1;
  }
}

.top-right {
  @apply grid;
  grid-template-columns: 1fr auto 30px;
  grid-template-rows: 1fr auto 50px;

  .image {
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 1;
    grid-row-end: 3;
  }

  & > div {
    grid-row-start: 2;
    grid-row-end: 4;
    grid-column-start: 2;
  }
}

